all_vessel_names.map:
function(doc) {
    if (doc.type == 'vessel') { 
      var regNum = (doc.coastGuardNumber ? doc.coastGuardNumber : doc.stateRegulationNumber);
      emit(doc.vesselName.toLowerCase(), doc.vesselName + ' (' + regNum + ')');
    }
}

all_port_names.map:
function(doc) {
    if (doc.type == 'port') { 
     const portNameInfo = (doc.code? doc.code + ': ': '') + doc.name + ' (' + doc.state + ')';
     emit(doc.name.toLowerCase(), portNameInfo);
    }
}
